import axios from "axios";
import { useEffect, useState } from "react";

function App() {
  const [todos, setTodos] = useState(null);
  useEffect(() => {
    axios.get("http://localhost:5000/todos").then((res) => setTodos(res.data));
  }, []);
  //화면에 나타나자마자 한번만 실행.

  console.log(todos);

  function todoList() {
    const todos = [
      { id: 1, text: "투두리스트 스타일링", done: true },
      { id: 2, text: "투두리스트 기능 구현하기", done: false },
      { id: 3, text: "리팩토링 하기", done: false },
    ];
  }
  return (
    <div>
      <div>
        <input type="text" />
        <button>등록</button>
      </div>
      <ul>
        <todoList>{todos[0]}</todoList>
        <todoList>{todos[1]}</todoList>
        <todoList>{todos[2]}</todoList>
      </ul>
    </div>
  );
}

export default App;
