import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

function PostItem() {
  const { PostId } = useParams(); //비구조 할당
  const [ids, setIds] = useState();

  const fetchData = async () => {
    let result = await axios.get("http://localhost:3000/posts/1");
    setIds(result.data);
  };

  useEffect(() => {
    fetchData();
  }, []);

  return (
    <div>
      <h1>글 제목 {ids.title}</h1>
      {/* title */}
      <p>글 자세히 보기...{ids.body}</p>
      {/* body */}
    </div>
  );
}

export default PostItem;
