import { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import styled from "styled-components";
import { tmdbAxios } from "../../api/tmdbAxios";

function MovieDetail() {
  const { pathname } = useLocation();
  const [movie, setMovie] = useState(null);

  const fetchData = async () => {
    const { data } = tmdbAxios.get(pathname);
    tmdbAxios.get(pathname);
  };

  useEffect(() => {}, []);
  return (
    <Container>
      <Wrapper>
        <ImgBox></ImgBox>
        <ContentBox>
          <Title>
            영화제목
            <span>(2023)</span>
          </Title>
          <SummaryBox>
            <h4>개요</h4>
            <p>영화 요약 및 내용 부분</p>
          </SummaryBox>
        </ContentBox>
      </Wrapper>
    </Container>
  );
}

const Container = styled.div`
  padding: 30px 0;

  background: #eee;
`;

const Wrapper = styled.div`
  display: flex;
  align-items: center;
  max-width: 900px;
  margin: 0 auto;
`;

const ImgBox = styled.div`
  width: 300px;
  height: 450px;
  background-color: red;
`;

const ContentBox = styled.div`
  margin-left: 20px;
`;

const Title = styled.h2`
  font-size: 1.5rem;
  font-weight: 700;

  span {
    font-size: 1.2rem;
    font-weight: 500;
  }
`;

const SummaryBox = styled.div`
  margin-top: 50px;
  h4 {
    font-size: 1.2rem;
    font-weight: 700;
    margin-bottom: 10px;
  }
`;

export default MovieDetail;
