// import styled from "styled-components";
import { useState } from "react";
import styled, { css } from "styled-components";

function TitleBox({ title, tabList }) {
  const [filterList, setFilterList] = useState([
    { id: 1, text: "오늘", url: "/trending/all/day", active: true },
    { id: 1, text: "이번주", url: "/trending/all/week", active: true },
  ]);

  const handleFilter = (id) => {
    setFilterList(
      filterList.map((filter) =>
        filter.id === id
          ? { ...filter, active: true }
          : {
              ...filter,
              done: false,
            }
      )
    );
  };

  return (
    <Container>
      <SectionTitle>{title}</SectionTitle>
      <TabList>
        {filterList.map((filter) => (
          <TabItem key={filter.id} active={filter.active}>
            {filter.text}
          </TabItem>
        ))}
      </TabList>
    </Container>
  );
}

const Container = styled.div`
  display: flex;
  align-items: center;
`;

const SectionTitle = styled.h2`
  font-size: 1.2rem;
`;

const TabList = styled.ul`
  display: flex;
  margin-left: 20px;
  border: 1px solid black;
`;

const TabItem = styled.li`
  padding: 5px;

  ${({ active }) =>
    active &&
    css`
      background-color: black;
      color: white;
    `}/* background-color: ${(props) => props.active && "black"};
      color: ${(props) => props.active && "white"}; */
`;

export default TitleBox;
