import { useCallback, useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import styled from "styled-components";
import { tmdbAxios } from "../../api/tmdbAxios";

function MovieDetail() {
  const { pathname } = useLocation();
  const [movie, setMovie] = useState(null);

  // movie가 null일 경우 {} 객체 비구조화 할당하므로 오류 발생 x
  const { title, overview, poster_path, backdrop_path } = movie ?? {};
  const url = "https://image.tmdb.org/t/p/w300" + poster_path;
  const backdrop_url = "https://image.tmdb.org/t/p/w300" + backdrop_path;

  const fetchData = useCallback(async () => {
    const { data } = await tmdbAxios.get(pathname);

    setMovie(data);
  }, [pathname]);

  useEffect(() => {
    fetchData();
  }, [fetchData]);

  if (!movie) return <div>로딩중...</div>;

  return (
    <Container style={{ background: backdrop_url }}>
      <Wrapper>
        <ImgBox>
          <img src={url} alt="" />
        </ImgBox>
        <ContentBox>
          <Title>
            {title}
            <span>(2023)</span>
          </Title>
          <SummaryBox>
            <h4>개요</h4>
            <p>{overview}</p>
          </SummaryBox>
        </ContentBox>
      </Wrapper>
    </Container>
  );
}

const Container = styled.div`
  padding: 30px 0;

  /* background: #eee; */
`;

const Wrapper = styled.div`
  display: flex;
  align-items: center;
  max-width: 900px;
  margin: 0 auto;
`;

const ImgBox = styled.div``;

const ContentBox = styled.div`
  margin-left: 20px;
`;

const Title = styled.h2`
  font-size: 1.5rem;
  font-weight: 700;

  span {
    font-size: 1.2rem;
    font-weight: 500;
  }
`;

const SummaryBox = styled.div`
  margin-top: 50px;
  h4 {
    font-size: 1.2rem;
    font-weight: 700;
    margin-bottom: 10px;
  }
`;

export default MovieDetail;
