      done: false,
    });
  };
  return (
    <div>
      <h1>게시물 목록</h1>
      <ul>
        <li>
          {/* / 주의 : 루트 의미  */}
          <Link to="1">제목1</Link>
        </li>
        <li>
          <Link to="2">제목2</Link>
        </li>
        <li>
          <Link to="3">제목3</Link>
        </li>
      </ul>
      <button onClick={handleSubmit}>
        <Link to="edit">작성하기</Link>
      </button>
    </div>
  );
}

export default PostList;
