  consst[(ids, setIds)] = useState();

  const fetchData = async () => {
    let result = await axios.get("http://localhost:3000/posts/1");
    setIds(result.data);
  };

  return (
    <div>
      <h1>글 제목 {PostId}</h1>
      {/* title */}
      <p>글 자세히 보기...</p>
      {/* body */}
    </div>
  );
}

export default PostItem;
